import "core-js/modules/es.array.push.js";
export default {
  components: {},

  data() {
    return {
      activeName: ''
    };
  },

  watch: {
    activeName(newName) {
      if (this.$route.path !== '/Despesas') {
        if (newName === "0") {
          this.$router.push('/Despesas');
        }
      }

      if (newName === "1") {
        this.$router.push('/relatorio');
      }

      if (newName === "2") {
        this.$router.push('/RegistroDespesas');
      }

      if (newName === "3") {
        this.$router.push('/Categorias');
      }
    }

  },

  mounted() {},

  methods: {}
};
